<?phpclass Anfitrion extends Eloquent {    protected   $table      = 'anfitrion';    public      $timestamps =   false;    private static $rules       = array(        'nombre'        =>  array('required', 'max:255'),        'apellidos'     =>  array('max:255'),        'email'         =>  array('email', 'max:255'),        'telefono'      =>  array('regex:/^\d{9}$/')    );    private static $messages    = array(        'max'               =>  'El campo :attribute no puede tener más de :max carácteres.',        'required'          =>  'El campo :attribute es requerido.',        'email'             =>  'El campo email no tiene un formato válido.',        'telefono.regex'    =>  'El campo teléfono tiene que tener 9 dígitos.'    );    public static function validate($input){        return Validator::make(            $input,            self::$rules,            self::$messages        );    }}