<?phpclass Entrada extends Eloquent {		public function contenido(){		return nl2br($this->contenido);	}	public function autor(){		return $this->belongsTo('User', 'user_id');	}	/**	 * Get the fecha en la que fue creada la entrada.	 *	 * @param \Carbon|null $date	 * @return string	 */	public function date($date=null){		if(is_null($date)) {			$date = $this->created_at;		}		return String::date($date);	}	/**	 * Get the URLs de la entrada.	 *	 * @return Array(string)	 */	/*public function urls()	{		return $this->belongsToMany('Enlace', 'enlaces_entradas');	}*/    public function enlaces(){        return $this->hasMany('Enlace');    }	/**	 * Returns la fecha de creacion de la entrada :)	 *	 * @return string	 */	public function created_at()	{		return $this->date($this->created_at);	}	/**	 * Returns la fecha de actualizacion de la entrada :)	 *	 * @return string	 */	public function updated_at()	{		return $this->date($this->updated_at);	}	public function fechainicio()	{		return $this->fecha_inicio;	}	/**	 * Devuelve el subtitulo de la entrada formateado	 *	 * @return string	 */	public function fechafin()	{		return $this->fecha_fin;	}    public static function borrarTodasLasImagenes(){        $path   = public_path().'/entradas';        return Directorio::VaciarArbolDeDirectorios($path, true);    }    public static function crearDirectorio(){        $path   = public_path();        return Directorio::create($path, 'entradas');    }}