<?phpclass Tutoria extends Eloquent {	public $timestamps = false;	/**	 * Devuelve el titulo de la entrada formateado	 *	 * @return string	 */	public function clase()	{		return $this->clase;	}	/**	 * Devuelve el subtitulo de la entrada formateado	 *	 * @return string	 */	public function email()	{		return $this->email;	}	/**	 * Get autor de la entrada.	 *	 * @return User	 */	public function autor()	{		return $this->belongsTo('User', 'user_id');	}	}