<?phpclass Ubicacion extends Eloquent {    protected   $table      =   'ubicacion';    public      $timestamps =   false;    private static $messages    =   array(        'required'          =>  'El campo :attribute debe ser rellenado.',        'titulo.max'        =>  'El campo título no puede tener más de :max carácteres.',        'descripcion.max'   =>  'El campo descripción no puede tener más de :max carácteres.',        'iframe.regex'      =>  'El campo iframe no tiene un formato válido.'    );    private static $rules       =   array(        'titulo'        =>  array('max:255'),        'descripcion'   =>  array('max:255'),        'iframe'        =>  array('required', 'regex:/<iframe(.*)src="([^"]*)"(.*)<\/iframe>/')    );    public static function validate($input){        return Validator::make(            $input,            self::$rules,            self::$messages        );    }}