<?phpclass Ubicacion extends Eloquent {    protected   $table      =   'ubicacion';    public      $timestamps =   false;    private static $rules       =   array(        'titulo'        =>  array('max:255'),        'descripcion'   =>  array('max:255'),        'iframe'        =>  array('required', 'regex:/<iframe(.*)src="([^"]*)"(.*)<\/iframe>/')    );    public static function validate($input){        return Validator::make(            $input,            self::$rules        );    }}